import React, { useState } from 'react';
import Homepage from './Homepage';
import { Button, Space, DatePicker, version } from 'antd';


const MenuBar = () => {
  const [selectedOption, setSelectedOption] = useState('home');

  const handleOptionClick = (option) => {
    setSelectedOption(option);
  };

  return (
    <div>


      <ul>
        <Button path="/" element={<Homepage />}>HOLA</Button>
        <li onClick={() => handleOptionClick('home')}>Home</li>
        <li onClick={() => handleOptionClick('about')}>About</li>
        <li onClick={() => handleOptionClick('contact')}>Contact</li>
      </ul>

      <div>
        {selectedOption === 'home' && <p>Welcome to the Home page!</p>}
        {selectedOption === 'about' && <p>Learn more about us on the About page.</p>}
        {selectedOption === 'contact' && <p>Contact us for more information.</p>}

      </div>
    </div>
  );
};

export default MenuBar;
